import {React, useState, useEffect} from 'react';
import "../Styles/Leaderboard.css"
import Entry from "./Entry.jsx"
import axios from 'axios'


const Leaderboard = (props) => {
    const [data, setData] = useState();
    let counter = 0;

    const getLeaderboard = () => {
        axios
            .get('http://localhost:4000/user/leaderboard')
            .then((data) => setData(data.data))
            .catch((error) => console.log(error));
    };

    useEffect(() => {
        getLeaderboard();
      }, []);

    const displayLeaderboard = (data) => {
        let result = []

        if (!data) {
            return result;
        }

        for (let n = 1; n < 6; n++) {
            result[n - 1] = <Entry pos={n} name={data[n - 1].username} score={data[n-1].score} />
        }

        return result;
    }

    return(
        <div className="leaderBoard">
            <div className="leaderboardTitle">HALL OF FAME</div>
            <div className="scores">
                <div className='scoreTitle'>
                    <div>Name</div>
                    <div>Total Steps</div>
                </div>
                {/* <Entry pos={1} name={data[0].username} score={data[0].score}/>
                <Entry pos={2} name={data[1].username} score={data[1].score}/>
                <Entry pos={3} name={data[2].username} score={data[2].score}/>
                <Entry pos={4} name={data[3].username} score={data[3].score}/>
                <Entry pos={5} name={data[4].username} score={data[4].score}/> */}
                {
                    data && data.map((d) => {
                        return <Entry pos={1} name={d.username} score={d.score} />
                    }
                )}
            </div>
        </div>
    );
}

export default Leaderboard;
